import React from 'react'
import styled from "styled-components";


const StyledSpinner = styled.div`



transform: scale(1);
animation: pulse 2s infinite;

@keyframes pulse {
	0% {
		transform: scale(0.95);

	}

	70% {
		transform: scale(1);

	}

	100% {
		transform: scale(0.95);

	}
}
`

function Spinner() {
    return (
        <StyledSpinner>
            <img src='https://cdn.glitch.com/3c3ffadc-3406-4440-bb95-d40ec8fcde72%2FLisaSimpson.png?1497567512083'/>
        </StyledSpinner>
    )
}

export default Spinner
