import React from 'react'
import styled from "styled-components";

import { COLOR, BORDER, FONT, SPACING } from "../constants";

const StyledCard = styled.article`

width: auto;
box-shadow: ${BORDER.SHADOW.M};
border-radius: ${BORDER.RADIUS.S};
display: flex;
align-items: center;
padding: ${SPACING.S};
justify-content: space-between;
flex-direction: ${rowOrder};

`

const StyledCardImage = styled.img`

width: 100px;
height: auto;
`

const StyledCardText = styled.p`

padding: ${SPACING.S};
`






function Card({quote}) {
    let rowOrder = quote.characterDirection == "Right" ? "row-reverse" : "row"

    return (
        <StyledCard >
            <StyledCardImage rowOrder={rowOrder} src={quote.image} />
            <StyledCardText>{quote.quote}</StyledCardText>
        </StyledCard>
    )
}

export default Card
