import React from 'react'
import styled from "styled-components";

import { COLOR, BORDER, FONT, SPACING } from "../constants";

const StyledCard = styled.article`

width: auto;
box-shadow: ${BORDER.SHADOW.M};
border-radius: ${BORDER.RADIUS.S};
display: flex;
align-items: center;
padding: ${SPACING.S};
justify-content: space-between;
flex-direction: ${(props) => props.rowOrder};


img{
    width: 100px;
}

p{
    padding: ${SPACING.S};
line-height: ${SPACING.M};
color: ${COLOR.BLACK};
background: ${COLOR.WHITE};
font-weight: ${FONT.WEIGHT.REGULAR};
font-family: ${FONT.FAMILY.BODY};

}

`









function Card({quote}) {
    let rowOrder = quote.characterDirection == "Right" ? "row-reverse" : "row"

    return (
        <StyledCard >
            <img rowOrder={rowOrder} src={quote.image} />
            <p>{quote.quote}</p>
        </StyledCard>
    )
}

export default Card
