import React from 'react'
import styled from "styled-components";

import { COLOR, BORDER, FONT, SPACING } from "../constants";

const StyledCard = styled.article`

width: 300px;
`

const StyledCardImage = styled.article`

width: 100px;
height: auto;
`




function Card({quote}) {
    return (
        <StyledCard>
            <StyledCardImage src={quote.image} />
            <p>{quote.quote}</p>
        </StyledCard>
    )
}

export default Card
